    let pattern = "(https?://)?(www\\.)?furrynovel\\.com/(zh|en|ja)/novel/\\d+(/chapter/\\d+)?"
    let fnWebpage = baseUrl.match(new RegExp(pattern))
    if (isHtml && fnWebpage) {
