function getUser(username, exactMatch) {
    // 修复传入object的bug
    username = String(username)
    let resp = getAjaxJson(util.urlSearchUsers(username))
    if (resp.users.length === 0) {
        return []
    }
    if (!exactMatch) {
        return resp.users
    }
    // 只返回用户名完全一样的用户
    return resp.users.filter(user => {
        return user.name === username
    })
}

// 包含所有小说数据
