                content = content.replace(`${matchedText}`, `${kanji}ï¼ˆ${kana}ï¼‰`)
            } else {
                var reg = new RegExp("[\\u4E00-\\u9FFF]+","g");
                if (reg.test(kana)) {
                    // kanaä¸ºä¸­æ–‡ï¼Œåˆ™æ›¿æ¢å›ã€Šä¹¦åå·ã€‹
                    content = content.replace(`${matchedText}`, `${kanji}ã€Š${kana}ã€‹`)
