            } else{
                // é˜…è¯»ä¸æ”¯æŒ <ruby> <rt> æ³¨éŸ³
                // content = content.replace(`${matchedText}`, `<ruby>${kanji}<rt>${kana}</rt></ruby>`)
                content = content.replace(`${matchedText}`, `${kanji}ï¼ˆ${kana}ï¼‰`)
            }
        }
    }

