@js:
var util = objParse(String(java.get("util")))

function objParse(obj) {
    return JSON.parse(obj, (n, v) => {
        if (typeof v == "string" && v.match("()")) {
            return eval(`(${v})`)
        }
        return v;
    })
}

function oneShotHandler(res) {
    let info = {}
    info.seriesId = undefined
    info.id = info.novelId = res.id
    info.title = info.latestChapter = res.title.replace(RegExp(/^\s+|\s+$/g), "")
    info.userName = res.userName
    info.tags = res.tags.tags.map(item => item.tag)
    info.tags.unshift('单本')
    // info.textCount = res.textCount  // 无数据
    info.textCount = res.userNovels[`${info.novelId}`].textCount
    info.description = res.description
    info.coverUrl = util.urlCoverUrl(res.coverUrl)
    info.detailedUrl = util.urlNovelUrl(info.novelId)
    info.catalogUrl = util.urlNovelDetailed(info.novelId)
    info.createDate = util.dateFormat(res.createDate)
    info.updateDate = util.dateFormat(res.uploadDate)

    info.readingTime = `${res.userNovels[`${info.novelId}`].readingTime / 60} 分钟`
    return info
}

function seriesHandler(res) {
    let info = {}
    info.seriesId = res.id
    info.id = info.novelId = res.firstNovelId
    info.title = res.title.replace(RegExp(/^\s+|\s+$/g), "")
    info.userName = res.userName
    info.tags = res.tags   //合并当前章节 tags
    info.tags.unshift('长篇')
    info.textCount = res.publishedTotalCharacterCount
    info.description = res.caption
    info.coverUrl = util.urlCoverUrl(res.cover.urls["480mw"]) // 240mw, 480mw, 1200x1200, 128x128, original
    info.detailedUrl = util.urlSeriesUrl(info.seriesId)
    info.catalogUrl = util.urlSeriesDetailed(info.seriesId)
    info.createDate = util.dateFormat(res.createDate)
    info.updateDate = util.dateFormat(res.updateDate)

    // 发送请求获取第一章 获取标签与简介
    let firstNovel = {}
    try {
        firstNovel = util.getAjaxJson(util.urlNovelDetailed(info.novelId)).body
        info.tags = info.tags.concat(firstNovel.tags.tags.map(item => item.tag))
    } catch (e) {  // 防止系列首篇无权限获取
        firstNovel = util.getAjaxJson(util.urlSeriesNovels(info.seriesId, 30, 0)).body.thumbnails.novel[0]
        info.id = info.novelId = firstNovel.id
        info.tags = info.tags.concat(firstNovel.tags)
    }
    info.tags = Array.from(new Set(info.tags))
    if (info.description === "") {
        info.description = firstNovel.description
    }

    info.readingTime = `${res.publishedReadingTime / 60} 分钟`
    info.latestChapter = ""
    info.language = res.language
    info.totalChapterNum = info.total = res.total  //章节总数
    info.latestNovelId = res.latestNovelId
    return info
}

function novelHandler(res){
    let info = {}
    if (res.firstNovelId !== undefined) {
        info = seriesHandler(res)
    } else {
        info = oneShotHandler(res)
    }
    info.tags = info.tags.join(",")
    if (util.MORE_INFO_IN_DESCRIPTION) {
        info.description = `\n书名：${info.title}\n作者：${info.userName}\n标签：${info.tags}\n上传：${info.createDate}\n更新：${info.updateDate}\n简介：${info.description}`
    } else {
        info.description = `\n${info.description}\n上传时间：${info.createDate}\n更新时间：${info.updateDate}`
    }
    return info
}

(function (res) {
    res = util.getNovelResSeries(result)
    // res = util.getNovelRes(result)
    // if (res.seriesNavData !== null) {     // 使用 util.getNovelRes(result) 时
    //     res = util.getAjaxJson(util.urlSeriesDetailed(res.seriesNavData.seriesId)).body
    // }
    try {
        return novelHandler(res)
    } catch (e) {
        java.log(e)
        java.log(`受 Pixiv 的限制，无法获取当前小说数据`)
    }
})();

ruleContent.content:
@js:
var util = objParse(String(java.get("util")))

function objParse(obj) {
    return JSON.parse(obj, (n, v) => {
        if (typeof v == "string" && v.match("()")) {
            return eval(`(${v})`)
        }
        return v;
    })
}

function getContent(res) {
    let content = res.content
    // 在正文内部添加小说描述
    if (res.description !== "") {
        content = res.description + "\n" + "——————————\n".repeat(2) + content
    }

    // 获取 [uploadedimage:] 的图片链接
    let hasEmbeddedImages = res.textEmbeddedImages !== undefined && res.textEmbeddedImages !== null
    if (hasEmbeddedImages) {
        Object.keys(res.textEmbeddedImages).forEach((key) => {
            content = content.replace(`[uploadedimage:${key}]`, `<img src="${res.textEmbeddedImages[key].urls.original}">`)
        })
    }

    // 获取 [pixivimage:] 的图片链接 [pixivimage:1234] [pixivimage:1234-1]
    let matched = content.match(RegExp(/\[pixivimage:(\d+)-?(\d+)]/gm))
    if (matched) {
        for (let i in matched) {
            let illustId, order
            let matched2 = matched[i].match(RegExp("(\\d+)-?(\\d+)"))
            let temp = matched2[0].split("-")
            illustId = temp[0]
            if (temp.length >= 2) {
                order = temp[1]
            }
            content = content.replace(`${matched[i]}`, `<img src="${util.urlIllustOriginal(illustId, order)}">`)
        }
    }

    // 替换 Pixiv 分页标记符号 [newpage]
    matched = content.match(RegExp(/[ 　]*\[newpage][ 　]*/gm))
    if (matched) {
        for (let i in matched){
            content = content.replace(`${matched[i]}`, `${"<p>​<p/>".repeat(3)}`)
        }
    }

    // 替换 Pixiv 章节标记符号 [chapter:]
    matched = content.match(RegExp(/\[chapter:(.*?)]/gm))
    if (matched) {
        for (let i in matched) {
            let matched2 = matched[i].match(/\[chapter:(.*?)]/m)
            let chapter = matched2[1].trim()
            content = content.replace(`${matched[i]}`, `${chapter}<p>​<p/>`)
        }
    }

    // 替换 Pixiv 跳转页面标记符号 [[jump:]]
    matched = content.match(RegExp(/\[jump:(\d+)]/gm))
    if (matched) {
        for (let i in matched) {
            let page = matched[i].match(/\d+/)
            content = content.replace(`${matched[i]}`, `\n\n跳转至第${page}节`)
        }
    }

    // 替换 Pixiv 链接标记符号 [[jumpuri: > ]]
    matched = content.match(RegExp(/\[\[jumpuri:(.*?)>(.*?)]]/gm))
    if (matched) {
        for (let i in matched) {
            let matched2 = matched[i].match(/\[\[jumpuri:(.*?)>(.*?)]]/m)
            let matchedText = matched2[0]
            let urlName = matched2[1].trim()
            let urlLink = matched2[2].trim()
            // 阅读不支持超链接
            //content = content.replace(`${matchedText}`, `<a href=${urlLink}>${urlName}</a>`)
            if (urlLink === urlName) {
                content = content.replace(`${matchedText}`, `${urlName}`)
            } else {
                content = content.replace(`${matchedText}`, `${urlName}: ${urlLink}`)
            }
        }
    }

    // 替换 Pixiv 注音标记符号 [[rb: > ]]
    matched = content.match(RegExp(/\[\[rb:(.*?)>(.*?)]]/gm))
    if (matched) {
        for (let i in matched) {
            let matched2 = matched[i].match(/\[\[rb:(.*?)>(.*?)]]/m)
            let matchedText = matched2[0]
            let kanji = matched2[1].trim()
            let kana = matched2[2].trim()

            if (!util.REPLACE_BOOK_TITLE_MARKS) {
                // 默认替换成（括号）
                content = content.replace(`${matchedText}`, `${kanji}（${kana}）`)
            } else {
                var reg = new RegExp("[\\u4E00-\\u9FFF]+","g");
                if (reg.test(kana)) {
                    // kana为中文，则替换回《书名号》
                    content = content.replace(`${matchedText}`, `${kanji}《${kana}》`)
                } else {
                    // 阅读不支持 <ruby> <rt> 注音
                    // content = content.replace(`${matchedText}`, `<ruby>${kanji}<rt>${kana}</rt></ruby>`)
                    content = content.replace(`${matchedText}`, `${kanji}（${kana}）`)
                }
            }
        }
    }
    return content + getComment(res)
}

function getComment(res) {
    let comments = ""
    let resp = util.getAjaxJson(util.urlNovelComments(res.id, 0, 50))
    if (resp.error === true){
        return ""
    }
    resp.body.comments.forEach(comment =>{
        comments += `${comment.userName}：${comment.comment}\n`
        if (comment.hasReplies === true) {
            let resp = util.getAjaxJson(util.urlNovelCommentsReply(comment.id, 1))
            if (resp.error === true) {
                return ""
            }
            resp.body.comments.reverse().forEach(reply =>{
                comments += `${reply.userName}(⤴️${reply.replyToUserName})：${reply.comment}\n`
            })
        }
    })
    if (comments) {
        comments = "\n" + "——————————\n".repeat(2) + "章节评论：\n" + comments
    }
    return comments
}

(function () {
    return getContent(util.getNovelRes(result))
})()

