@js:
var util = objParse(String(java.get("util")))

function objParse(obj) {
    return JSON.parse(obj, (n, v) => {
        if (typeof v == "string" && v.match("()")) {
            return eval(`(${v})`)
        }
        return v;
    })
}

function urlNovel(novelId){
    if (util.SHOW_ORIGINAL_NOVEL_LINK) {
        return urlNovelUrl(novelId)
    } else {
        return urlNovelDetailed(novelId)
    }
}

function oneShotHandler(res) {
    res.textCount = res.userNovels[`${res.id}`].textCount
    res.createDate = timeTextFormat(res.createDate)
    return [{
        title: res.title.replace(RegExp(/^\s+|\s+$/g), ""),
        chapterUrl: urlNovel(res.id),
        chapterInfo: `${res.createDate}ã€€ã€€${res.textCount}å­—`
    }]
}

function seriesHandler(res) {
    const limit = 30
    let returnList = []
    let seriesID = 0, allChaptersCount = 0
    if (res.seriesNavData !== undefined) {
        seriesID = res.seriesNavData.seriesId
        allChaptersCount = getAjaxJson(urlSeriesDetailed(seriesID)).body.total
    } else {
        seriesID = res.id
        allChaptersCount = res.total
    }
    util.debugFunc(() => {
        java.log(`æœ¬ç³»åˆ— ${seriesID} ä¸€å…±æœ‰${allChaptersCount}ç« `);
    })

    //å‘é€è¯·æ±‚è·å¾—ç›¸åº”æ•°é‡çš„ç›®å½•åˆ—è¡¨
    function sendAjaxForGetChapters(lastIndex) {
        res = getAjaxJson(urlSeriesNovels(seriesID, limit, lastIndex)).body.thumbnails.novel
        res.forEach(v => {
            v.title = v.title.replace(RegExp(/^\s+|\s+$/g), "").replace(RegExp(/ï¼ˆ|ï¼‰|-/g), "")
            v.chapterUrl = urlNovel(v.id)
            v.updateDate = timeTextFormat(v.createDate)
            v.chapterInfo = `${v.updateDate}ã€€ã€€${v.textCount}å­—`
            util.debugFunc(() => {
                java.log(`${v.title}`)
            })
        })
        return res;
    }

    //é€»è¾‘æ§åˆ¶è€… ä¹Ÿå°±æ˜¯ä½¿ç”¨ä¸Šé¢å®šä¹‰çš„ä¸¤ä¸ªå‡½æ•°æ¥åšå¯¹åº”åŠŸèƒ½
    //è¦çˆ¬å–çš„æ€»æ¬¡æ•°
    let max = (allChaptersCount / limit) + 1
    for (let i = 0; i < max; i++) {
        //java.log("içš„å€¼:"+i)
        let list = sendAjaxForGetChapters(i * limit);
        //å–å‡ºæ¯ä¸ªå€¼
        returnList = returnList.concat(list)
        // java.log(JSON.stringify(returnList))
    }
    return returnList
}

(function (res) {
    res = util.getNovelResSeries(result)
    if (res.firstNovelId !== undefined || res.seriesNavData !== null) {
        return seriesHandler(res)
    } else {
        return oneShotHandler(res)
    }
})()

