    "excited3": 309, "love3": 310, "normal4": 401, "surprise4": 402, "series4": 403,
    "love4": 404, "shine4": 405, "sweet4": 406, "shame4": 407, "sleep4": 408,
    "heart": 501, "teardrop": 502, "star": 503
