            res = util.getAjaxJson(util.urlNovelDetailed(novelId))
        }
        if (res.error === true || res.body.total === 0) {
            java.log(`æ— æ³•ä» Pixiv è·å–å½“å‰å°è¯´`)
            return []
        }
        return res.body
